import random
import base64
image=""
k=0
print(" Correctly solve 1000 'a + b' problems to earn the flag.")
while True:
    a = random.randint(0, 100)
    b = random.randint(0, 100)
    print(f"{a} + {b} = ")
    
    try:
        c = int(input())  
    except ValueError:
        print("Error: Please enter a valid number.")
        continue  
    
    if a + b == c:
        k += 1
        print(f"Correct! Score: {k}")
    else:
        print(f"Wrong! The answer was {a + b}. Score: {k}")
    
    if k == 1000:
        
        if pow(4, 2) == -1:  
            with open("flag.png", "wb") as f:
                f.write(base64.b64decode(image))
        print("You are good at math, but I'm not. You will understand.")
        break